    import java.io.*;
    import java.util.*;

    public class Main {
        /**
        * 8ë°©í–¥
        *(-1,-1)  (-1,0)  (-1,1)
        *( 0,-1)   x,y    ( 0,1)
        * ( 1,-1)  ( 1,0)  ( 1,1)
        */
        static int[] dx = {-1, -1, -1, 0, 0, 1, 1, 1};
        static int[] dy = {-1, 0, 1, -1, 1, -1, 0, 1};

        static int[][] arr = null;
        static boolean[][] visited = null;

        static int h = 0;
        static int w = 0;

        public static void main(String[] args) throws Exception {
            BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
            while (true) {
                String[] tmpArr = br.readLine().split(" ");

                w = Integer.parseInt(tmpArr[0]);
                h = Integer.parseInt(tmpArr[1]);

                if (w == 0 && h == 0) {
                    break;
                }

                arr = new int[h][w];
                visited = new boolean[h][w];

                // ì…ë ¥ ë‹¤ ë°›ê¸°
                for (int i=0; i<h; i++) {
                    String[] strArr = br.readLine().split(" ");
                    for (int j=0; j<w; j++) {
                        arr[i][j] = Integer.parseInt(strArr[j]);
                    }
                }

                int cnt = 0;
                for (int i=0; i<h; i++) {
                    for (int j=0; j<w; j++) {
                        // ìœ¡ì§€ì´ë©´ì„œ ë°©ë¬¸ ì•ˆí•œ ê²½ìš°ì—ë§Œ
                        if (arr[i][j] == 1 && !visited[i][j]) {
                            dfs(i, j);
                            // bfs(i, j);
                            cnt++;
                        }
                    }
                }

                System.out.println(cnt);
            }
        }

        public static void dfs(int x, int y) {
            // ë°©ë¬¸ í‘œì‹œ
            visited[x][y] = true;

            // ëŒ€ê°ì„ ê¹Œì§€ í¬í•¨í•´ì„œ 8ë°©í–¥ íƒìƒ‰
            for (int i=0; i<8; i++) {
                int nx = x + dx[i];   // ë‹¤ìŒ í–‰
                int ny = y + dy[i];   // ë‹¤ìŒ ì—´
                // ë°°ì—´ ë²”ìœ„ ì•ˆì¸ì§€ ì²´í¬
                if (nx >= 0 && ny >= 0 && nx < h && ny < w) {
                    // ìœ¡ì§€ì´ë©´ì„œ ë°©ë¬¸í•˜ì§€ ì•Šì•˜ë‹¤ë©´ DFS ì¬ê·€ í˜¸ì¶œ
                    if (arr[nx][ny] == 1 && !visited[nx][ny]) {
                        dfs(nx, ny);
                    }
                }
            }
        }

        public static void bfs(int x, int y) {
            Queue<int[]> queue = new LinkedList<>();
            queue.offer(new int[]{x, y});
            visited[x][y] = true;

            while (!queue.isEmpty()) {
                int[] now = queue.poll();
                int curX = now[0];
                int curY = now[1];

                for (int i = 0; i < 8; i++) {
                    int nx = curX + dx[i];
                    int ny = curY + dy[i];

                    if (nx >= 0 && ny >= 0 && nx < h && ny < w) {
                        if (arr[nx][ny] == 1 && !visited[nx][ny]) {
                            visited[nx][ny] = true;  // ë°©ë¬¸ ì²˜ë¦¬
                            queue.offer(new int[]{nx, ny});
                        }
                    }
                }
            }
        }
    }

    ````

