    import java.util.*;
    class Solution {
        class Stage {
            int index;
            double rate;

            Stage(int index, double rate) {
                this.index = index;
                this.rate = rate;
            }
        }

        public int[] solution(int N, int[] stages) {
            int[] answer = {};

            List<Stage> list = new ArrayList<>();
            // 1. ìŠ¤í…Œì´ì§€ ê°œìˆ˜ ë§Œí¼ forë¬¸ ìˆ˜í–‰
            for (int i=1; i<=N; i++) {
                // ì‹¤íŒ¨ìœ¨ ê³„ì‚°
                double rate = calc(stages, i);
                // listì— ë‹´ê¸°
                list.add(new Stage(i, rate));
