        private double calc(int[] stages, int stage) {
            int fail = 0;
            int sucess = 0;

            for (int i=0; i<stages.length; i++) {
                if (stages[i] >= stage) { // í•´ë‹¹ ìŠ¤í…Œì´ì§€ì— ë„ë‹¬í•œ ì‚¬ìš©ì
                    sucess++;
                }
                if (stages[i] == stage) { // í•´ë‹¹ ìŠ¤í…Œì´ì§€ì—ì„œ ì‹¤íŒ¨í•œ ì‚¬ìš©ì
                    fail++;
                }
            }

            if (sucess == 0) {
                return 0.0;
            } else {
                return (double) fail / sucess;
            }
        }
    }
