    import java.io.*;
    import java.util.*;

    public class Main {
        static int[][] arr = null;
        static boolean[][] visited = null;
        static int result = 0;

        static int m = 0;
        static int n = 0;

        // ìƒí•˜ì¢Œìš° ì´ë™
        static int[] dx = {-1, 1, 0, 0};
        static int[] dy = {0, 0, -1, 1};

        public static void main(String[] args) throws IOException {
            String[] tmpArr = null;

            // 1. ì…ë ¥
            BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
            // í…ŒìŠ¤íŠ¸ì¼€ì´ìŠ¤
            int t = Integer.parseInt(br.readLine());

            for (int index=0; index<t; index++) {
                tmpArr = br.readLine().split(" ");
                // ë°°ì¶”ë°­ì˜ ê°€ë¡œê¸¸ì´ M
                m = Integer.parseInt(tmpArr[0]);
                // ë°°ì¶”ë°­ì˜ ì„¸ë¡œê¸¸ì´ N
                n = Integer.parseInt(tmpArr[1]);
                // ë°°ì¶”ê°€ ì‹¬ì–´ì ¸ ìˆëŠ” ìœ„ì¹˜ì˜ ê°œìˆ˜ K
                int k = Integer.parseInt(tmpArr[2]);

                // 2. ì¸ì ‘í–‰ë ¬ ë§Œë“¤ê¸°
                arr = new int[m][n];

                for (int i=0; i<k; i++) {
                    tmpArr = br.readLine().split(" ");
                    int a = Integer.parseInt(tmpArr[0]);
                    int b = Integer.parseInt(tmpArr[1]);

    //                System.out.println(a + " / " + b);

                    arr[a][b] = 1;
                }

                // 3. ë°©ë¬¸ë°°ì—´ ë§Œë“¤ê¸°
                visited = new boolean[m][n];

                result = 0;

                // 4. íƒìƒ‰
                for (int i=0; i<m; i++) {
                    for (int j=0; j<n; j++) {
                        if (arr[i][j] == 1 && !visited[i][j]) {
                            bfs(i, j);
                            result++;
                        }
                    }
                }

                // 5. ì¶œë ¥
                System.out.println(result);
            }
        }

        static void bfs (int x, int y) {
            visited[x][y] = true;

            Queue<int[]> queue = new LinkedList<>();
            queue.add(new int[] {x, y});

            while(!queue.isEmpty()) {
                int[] node = queue.poll();

                int nowX = node[0];
                int nowY = node[1];

                for (int i=0; i<4; i++) {
                    int nx = nowX + dx[i];
                    int ny = nowY + dy[i];

                    if (nx >= 0 && ny >= 0 && nx < m && ny < n) {
                        if (arr[nx][ny] == 1 && !visited[nx][ny]) {
                            queue.add(new int[] {nx, ny});
                            visited[nx][ny] = true;
                        }
                    }
                }
            }
        }
    }
    ``` 

