            BufferedReader br = new BufferedReader(new InputStreamReader(System.in));

            String[] tmp1 = br.readLine().split(" ");
            String[] tmp2 = br.readLine().split(" ");

            int n = Integer.parseInt(tmp1[0]);    // ì˜¨ë„ë¥¼ ì¸¡ì •í•œ ì „ì²´ ë‚ ì§œì˜ ìˆ˜
            int k = Integer.parseInt(tmp1[1]) - 1;    // í•©ì„ êµ¬í•˜ê¸° ìœ„í•œ ì—°ì†ì ì¸ ë‚ ì§œì˜ ìˆ˜(ìê¸° ìì‹ ë„ í¬í•¨ë˜ì–´ì„œ -1)

